import React, { useState } from "react";
import Header from "../../Components/Header/Header";
import Dropdown from "react-dropdown";
import "react-dropdown/style.css";
import "./Listing.css";
import { useQuery, useInfiniteQuery } from "react-query";
import { getUser, getVendors } from "../../utils/profile_helper";
import { Image, Spin } from "antd";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";

const options = ["All","Recent", "Oldest"];
const filters = ["Sort by A-Z", "Sort by Z-A", "Sort by location"];
const defaultOption = options[0];

const Listing = () => {
  const navigate = useNavigate();
  const { data: profile, isLoadin: isLoading3 } = useQuery("profile", getUser);
  const [isLoading, setIsLoading] = useState(false);
  const [sortBy, setSortBy] = useState({
    id: 0,
    column: "created_at",
    ascending: true,
  });

  const {
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    data: vendors,
  } = useInfiniteQuery(
    ["vendors", profile?.id, sortBy?.id],
    ({ pageParam = 0 }) => getVendors(pageParam, sortBy),
    {
      getNextPageParam: (lastPage, allPages) => {
        return allPages?.length;
      },
    }
  );

  async function increasePage() {
    try {
      setIsLoading(true);
      if (hasNextPage) {
        await fetchNextPage();
        setIsLoading(false);
      }
    } catch (err) {
      setIsLoading(false);
    }
  }

  // useEffect(() => {
  //   console.log(vendors);
  // }, [vendors]);

  return (
    <>
      <Header />
      <div className="filter">
        <Dropdown
          options={filters}
          value={"Filter"}
          placeholder={"Filter"}
          onChange={(e) => {
            if (e.value === "Sort by A-Z") {
              setSortBy({
                id: 2,
                column: "display_name",
                ascending: true,
              });
            } else if (e.value === "Sort by Z-A") {
              setSortBy({
                id: 3,
                column: "display_name",
                ascending: false,
              });
            } else if (e.value === "Sort by location") {
              setSortBy({
                id: 4,
                column: "location",
                ascending: true,
              });
            }
          }}
        />
        <Dropdown
          options={options}
          value={defaultOption}
          placeholder="Recent"
          onChange={(e) => {
            if (e.value === "Recent") {
              setSortBy({
                id: 1,
                column: "created_at",
                ascending: false,
              });
            } else {
              setSortBy({
                id: 0,
                column: "created_at",
                ascending: true,
              });
            }
          }}
        />
      </div>
      <hr />
      <div className="cardcontainer">
        {vendors?.pages?.map((page) => {
          return page?.map((vendor) => {
            return (
              <div
                className="listingcard"
                key={vendor?.id}
                onClick={() => navigate(`/profile/${vendor?.id}`)}
              >
                {vendor?.profile_pic ? (
                  <img src={vendor?.profile_pic} alt="bg" />
                ) : vendor?.cover_pic ? (
                  <img src={vendor?.cover_pic} alt="bg" />
                ) : (
                  <Image
                    width={"44%"}
                    height={220}
                    src="error"
                    fallback="data:image/png;base64,"
                    preview={false}
                  />
                )}

                <div className="listingname">
                  <p>{vendor?.display_name}</p>
                  <p>{vendor?.location}</p>
                  <p>{vendor?.bio}</p>
                </div>
              </div>
            );
          });
        })}
      </div>
      <div className="load">
        {isLoading ? (
          <Spin />
        ) : (
          <button onClick={increasePage}>Load more</button>
        )}
      </div>
      <br />
    </>
  );
};

export default Listing;
